(function(sc, describe, it, beforeEach, afterEach, expect, _) {
  var addedFiles, folderId, fatalError = false;

  sc.app.on("upload-fileAdded", function(fileModel) {
    addedFiles.push(fileModel);
  });

  sc.app.on("upload-info-deleted", function(fileModel) {
    addedFiles = _.without(addedFiles, fileModel);
  });

  describe("Given an UploaderInfo control with default property values", function() {
    var uploader = sc.app.Uploader1,
      uploaderInfo = sc.app.UploaderInfo1,
      $element = $(uploaderInfo.el);

    it("info panel should be empty", function() {
      expect($element.find("div").length).toBe(1);
    });

    describe("when a file is added", function() {
      beforeEach(function(done) {
        addedFiles = [];
        addJpgFile("firstFile", uploader.$el);


        wait(function() {
          return addedFiles.length === 1;
        }, done);
      });

      afterEach(function() {
        for (var i = addedFiles.length - 1; i >= 0; i--) {
          sc.app.trigger("upload-info-deleted", addedFiles[i]);
        }

        var options = uploader.$el.find(".sc-uploader-fileupload").data("blueimp-fileupload").options;
        options.fileInput.context.form.__ids = [];

        uploaderInfo.Files.reset();
        uploaderInfo.Files.reset();
      });

      it("the Name filed should be shown", function() {
        expect($element.find("input[name='name']").is(":visible")).toBe(true);
      });

      it("the Description filed should be shown", function() {
        expect($element.find("textarea[name='description']").is(":visible")).toBe(true);
      });

      it("the Alternate Text filed should be shown", function() {
        expect($element.find("input[name='alternate']").is(":visible")).toBe(true);
      });

      it("file info should appear", function() {
        expect($element.find("div.sc-uploaderInfo-row").is(":visible")).toBe(true);
        expect($element.find("div.sc-uploaderInfo-row").length).toBe(1);
      });

      describe("when Alternate Text field is updated", function() {
        var uploadInfoUpdatedTriggered;

        beforeEach(function() {
          uploadInfoUpdatedTriggered = false;
          sc.app.once("upload-info-updated", function(file) {
            uploadInfoUpdatedTriggered = file.alternate === "new alt";
          });

          changeValue($element.find("input[name='alternate']"), "new alt");
        });

        it("the 'upload-info-updated' event should be triggered", function() {
          expect(uploadInfoUpdatedTriggered).toBe(true);
        });
      });

      describe("when the SpeakTest foler is created in media library", function() {
        beforeEach(createTestFolder);
        afterEach(removeTestFolder);

        describe("when file uploading is triggered", function() {
          var wasProgressVisible,
            wasRemoveEnabled;

          beforeEach(function(done) {
            wasProgressVisible = false;
            wasRemoveEnabled = false;

            addedFiles[0].once("change:percentage", function () {
              if (uploaderInfo.Files.array[0].percentage === "100") {
                uploaderInfo.Files.array[0].percentage = "50";
              }

              wasProgressVisible = $element.find("div.progress").is(":visible");
              wasRemoveEnabled = $element.find("button.remove").is(":enabled");
            });

            uploader.on("uploadCompleted", done);
            uploader.upload();
          });

          it("input controls should be disabled", function() {
            expect($element.find("input").is(":enabled")).toBe(false);
            expect($element.find("textarea").is(":enabled")).toBe(false);
          });

          it("the 'Ready to upload' label should be hidden", function() {
            expect($element.find("div.ready").is(":visible")).toBe(false);
          });

          it("progress indicator should be visible during upload", function() {
            expect(wasProgressVisible).toBe(true);
            expect($element.find("div.progress").is(":visible")).toBe(false);
          });

          it("the 'Remove' button should be disabled during uploading", function() {
            expect(wasRemoveEnabled).toBe(false);
          });

          it("the 'Remove' button should be hidden after uploading", function() {
            expect($element.find("button.remove").is(":visible")).toBe(false);
          });

          it("the 'Upload completed' label should be visible", function() {
            expect($element.find("div.completed").is(":visible")).toBe(true);
          });
        });
      });

      describe("when upload error happens", function() {
        beforeEach(function() {
          sc.app.trigger("upload-error", { id: addedFiles[0].id });
        });

        it("input controls should be disabled", function() {
          expect($element.find("input").is(":enabled")).toBe(false);
          expect($element.find("textarea").is(":enabled")).toBe(false);
        });

        it("the 'Remove' button should be hidded", function() {
          expect($element.find("button.remove").is(":visible")).toBe(false);
        });

        it("progress indicator should be hidded", function() {
          expect($element.find("div.progress").is(":visible")).toBe(false);
        });

        it("the 'Ready to upload' label should be hidden", function() {
          expect($element.find("div.ready").is(":visible")).toBe(false);
        });

        it("the 'Upload failed' label should be visible", function() {
          expect($element.find("div.failed").is(":visible")).toBe(true);
        });
      });

      describe("when file is removed", function() {
        var scUploaderRemoveTriggered,
          uploadInfoDeletedTriggered;

        sc.app.on("sc-uploader-remove", function(file) {
          scUploaderRemoveTriggered = file.name === "firstFile";
        });

        beforeEach(function() {
          scUploaderRemoveTriggered = false;
          uploadInfoDeletedTriggered = false;

          sc.app.once("upload-info-deleted", function(file) {
            uploadInfoDeletedTriggered = file.name === "firstFile";
          });

          $element.find(".remove").click();
        });

        it("the 'sc-uploader-remove' event should be triggered", function() {
          expect(scUploaderRemoveTriggered).toBe(true);
        });

        it("the 'upload-info-deleted' event should be triggered", function() {
          expect(uploadInfoDeletedTriggered).toBe(true);
        });

        it("file model should be removed from control collection", function() {
          expect(uploaderInfo.Files.length).toBe(0);
          expect($element.find("div.sc-uploaderInfo-row").length).toBe(0);
        });
      });
    });
  });

  function createTestFolder(done) {
    if (fatalError) {
      throw "Fatal Error";
    }

    var onError = function() {
      fatalError = true;
      done();
    };

    $.ajax({
      type: "POST",
      url: "/sitecore/api/ssc/item/sitecore%2Fmedia%20library%2Ffiles?database=master",
      data: '{ "ItemName": "SpeakTest", "TemplateID": "FE5DD826-48C6-436D-B87A-7C4210C7413B" }',
      contentType: "application/json; charset=utf-8",
      success: function() {
        $.get("/sitecore/api/ssc/item?path=/sitecore/media library/files/SpeakTest&database=master")
          .done(function(item) {
            folderId = item.ItemID;
            done();
          })
          .fail(onError);
      },
      error: onError
    });
  }

  function removeTestFolder(done) {
    if (fatalError) {
      throw "Fatal Error";
    }

    $.ajax({
      type: "DELETE",
      url: "/sitecore/api/ssc/item/{" + folderId + "}?database=master",
      error: function() { fatalError = true; },
      complete: done
    });
  }

  function wait(check, done, interval, maxDuration) {
    interval = interval || 25;
    maxDuration = maxDuration || 5000;

    var intervalId = setInterval(function() {
      if (check()) {
        done();
        clearInterval(intervalId);
      }
    }, interval);

    setTimeout(function() {
      clearInterval(intervalId);
    }, maxDuration);
  }

  function addJpgFile(filename, $element) {
    var file = createJpgFile(filename);
    $element.find("input").fileupload("add", { files: [file] });
    return file;
  }

  function createJpgFile(filename) {
    var b64Content = "";

    var blob = new Blob(b64toBlobContent(b64Content), { type: "image/jpeg" });
    blob.name = filename + ".jpg";

    return blob;
  }

  function b64toBlobContent(b64Data, sliceSize) {
    sliceSize = sliceSize || 512;

    var byteCharacters = atob(b64Data);
    var byteArrays = [];

    for (var offset = 0; offset < byteCharacters.length; offset += sliceSize) {
      var slice = byteCharacters.slice(offset, offset + sliceSize);

      var byteNumbers = new Array(slice.length);
      for (var i = 0; i < slice.length; i++) {
        byteNumbers[i] = slice.charCodeAt(i);
      }

      var byteArray = new Uint8Array(byteNumbers);

      byteArrays.push(byteArray);
    }

    return byteArrays;
  }

  function changeValue($target, value) {
    $target.val(value);

    var target = $target[0];
    var event = document.createEvent("HTMLEvents");
    event.initEvent("change", true, true, window, 0, 0, 0, 0, 0, false, false, false, false, 0, target);
    target.dispatchEvent(event);
  }
})(Sitecore.Speak, describe, it, beforeEach, afterEach, expect, _);